import React, { useState, useEffect } from "react";

/**
 * StaffUpload.jsx
 *
 * Props:
 * - token: current JWT token (string)
 * - setToken: function to update token at parent (App.jsx)
 *
 * Behavior:
 * - If not logged in, shows login form (POST /api/auth/login)
 * - If logged in, shows file input and uploads file to POST /api/upload-marks
 * - Expects backend endpoints:
 *    POST /api/auth/login  { email, password }  -> { token }
 *    POST /api/upload-marks  (multipart/form-data) with Authorization header
 */

export default function StaffUpload({ token, setToken }) {
  const [email, setEmail] = useState("admin@example.com");
  const [password, setPassword] = useState("");
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loginError, setLoginError] = useState("");
  const [uploadResult, setUploadResult] = useState(null);
  const [uploadError, setUploadError] = useState("");

  useEffect(() => {
    // If token exists in localStorage but not in state, sync
    const local = localStorage.getItem("token");
    if (local && !token) {
      setToken(local);
    }
  }, [token, setToken]);

  async function handleLogin(e) {
    e.preventDefault();
    setLoginError("");
    if (!email || !password) {
      setLoginError("Enter email and password.");
      return;
    }
    setLoading(true);
    try {
      const res = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password })
      });
      const j = await res.json();
      if (!res.ok) {
        setLoginError(j.error || "Login failed");
      } else {
        const t = j.token;
        localStorage.setItem("token", t);
        setToken(t);
        setPassword("");
      }
    } catch (err) {
      console.error(err);
      setLoginError("Network error during login.");
    } finally {
      setLoading(false);
    }
  }

  async function handleUpload(e) {
    e.preventDefault();
    setUploadError("");
    setUploadResult(null);

    if (!file) {
      setUploadError("Choose an Excel file to upload.");
      return;
    }
    if (!token && !localStorage.getItem("token")) {
      setUploadError("You must be logged in to upload.");
      return;
    }

    setLoading(true);
    try {
      const form = new FormData();
      form.append("file", file);

      const res = await fetch("/api/upload-marks", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token") || token}`
        },
        body: form
      });

      const j = await res.json();
      if (!res.ok) {
        setUploadError(j.error || j.details || "Upload failed");
      } else {
        setUploadResult(j);
      }
    } catch (err) {
      console.error(err);
      setUploadError("Network error during upload.");
    } finally {
      setLoading(false);
    }
  }

  function handleLogout() {
    localStorage.removeItem("token");
    setToken("");
    setUploadResult(null);
    setUploadError("");
  }

  // If logged-in show upload UI
  const loggedIn = Boolean(token || localStorage.getItem("token"));

  return (
    <div style={styles.wrap}>
      <h2>Staff Upload</h2>

      {!loggedIn ? (
        <div>
          <h3>Staff Login</h3>
          <form onSubmit={handleLogin} style={styles.form}>
            <label style={styles.label}>
              Email
              <input
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                style={styles.input}
                type="email"
                placeholder="staff@example.com"
              />
            </label>

            <label style={styles.label}>
              Password
              <input
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                style={styles.input}
                type="password"
                placeholder="your password"
              />
            </label>

            <button style={styles.button} type="submit" disabled={loading}>
              {loading ? "Logging in..." : "Login"}
            </button>
            {loginError && <div style={styles.error}>{loginError}</div>}
          </form>
        </div>
      ) : (
        <div>
          <div style={styles.loggedInBar}>
            <div>Logged in as <b>{localStorage.getItem("token") ? "staff" : "staff"}</b></div>
            <button onClick={handleLogout} style={styles.logoutBtn}>Logout</button>
          </div>

          <h3>Upload Excel file (marks)</h3>
          <form onSubmit={handleUpload} style={styles.form}>
            <input
              type="file"
              accept=".xlsx,.xls,.csv"
              onChange={(e) => setFile(e.target.files[0])}
            />
            <div style={{ marginTop: 8 }}>
              <button style={styles.button} type="submit" disabled={loading}>
                {loading ? "Uploading..." : "Upload"}
              </button>
            </div>
          </form>

          {uploadError && <div style={styles.error}>{uploadError}</div>}

          {uploadResult && (
            <div style={styles.resultBox}>
              <h4>Upload result</h4>
              <pre style={{ whiteSpace: "pre-wrap" }}>
                {JSON.stringify(uploadResult, null, 2)}
              </pre>
            </div>
          )}

          <div style={{ marginTop: 12, fontSize: 13, color: "#444" }}>
            <strong>Note:</strong> Excel must contain columns: <code>register_number</code>, <code>subject_code</code>, <code>marks</code>. Optional: <code>student_name</code>, <code>dob</code>.
          </div>
        </div>
      )}
    </div>
  );
}

const styles = {
  wrap: {
    maxWidth: 760,
    margin: "0 auto",
    padding: 20,
    fontFamily: "system-ui, -apple-system, 'Segoe UI', Roboto, 'Helvetica Neue', Arial"
  },
  form: {
    display: "grid",
    gap: 10,
    marginTop: 8
  },
  label: {
    display: "flex",
    flexDirection: "column",
    fontSize: 14
  },
  input: {
    padding: "8px 10px",
    fontSize: 14,
    marginTop: 6,
    borderRadius: 4,
    border: "1px solid #ccc"
  },
  button: {
    width: 140,
    padding: "8px 12px",
    background: "#1f6feb",
    color: "white",
    border: "none",
    borderRadius: 6,
    cursor: "pointer"
  },
  error: {
    marginTop: 10,
    color: "crimson"
  },
  resultBox: {
    marginTop: 12,
    background: "#f7f7f7",
    padding: 12,
    borderRadius: 6
  },
  loggedInBar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    background: "#eef",
    padding: 10,
    borderRadius: 6,
    marginBottom: 12
  },
  logoutBtn: {
    background: "tomato",
    color: "white",
    padding: "6px 10px",
    border: "none",
    borderRadius: 4,
    cursor: "pointer"
  }
};
