import React, { useState } from "react";

/**
 * StudentLookup.jsx
 * Simple component for students to enter register number + DOB and view marks.
 *
 * Assumptions:
 * - Backend endpoint: POST /api/students/verify-and-get
 * - Request body: { register_number, dob }
 * - Response: { register_number, marks: [ { register_number, subject_code, marks, uploaded_by, uploaded_at }, ... ] }
 */

export default function StudentLookup() {
  const [registerNumber, setRegisterNumber] = useState("");
  const [dob, setDob] = useState(""); // expected "YYYY-MM-DD" (text input)
  const [loading, setLoading] = useState(false);
  const [marks, setMarks] = useState(null);
  const [error, setError] = useState("");

  async function handleSubmit(e) {
    e.preventDefault();
    setError("");
    setMarks(null);

    if (!registerNumber.trim() || !dob.trim()) {
      setError("Please enter both Register Number and Date of Birth.");
      return;
    }

    setLoading(true);
    try {
      const res = await fetch("/api/students/verify-and-get", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          register_number: registerNumber.trim(),
          dob: dob.trim()
        })
      });

      const data = await res.json();

      if (!res.ok) {
        setError(data.error || "Failed to fetch marks. Try again.");
        setLoading(false);
        return;
      }

      setMarks(data.marks || []);
    } catch (err) {
      console.error(err);
      setError("Network error. Make sure the backend is running.");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div style={styles.wrap}>
      <h2 style={styles.h2}>Student Lookup</h2>

      <form onSubmit={handleSubmit} style={styles.form}>
        <label style={styles.label}>
          Register Number
          <input
            value={registerNumber}
            onChange={(e) => setRegisterNumber(e.target.value)}
            style={styles.input}
            placeholder="e.g. REG12345"
          />
        </label>

        <label style={styles.label}>
          Date of Birth (YYYY-MM-DD)
          <input
            value={dob}
            onChange={(e) => setDob(e.target.value)}
            style={styles.input}
            placeholder="2004-05-12"
          />
        </label>

        <button type="submit" style={styles.button} disabled={loading}>
          {loading ? "Checking..." : "View Marks"}
        </button>
      </form>

      {error && <div style={styles.error}>{error}</div>}

      {marks && (
        <div style={styles.results}>
          <h3>Marks for {registerNumber}</h3>
          {marks.length === 0 ? (
            <div>No marks found for this student.</div>
          ) : (
            <table style={styles.table}>
              <thead>
                <tr>
                  <th style={styles.th}>Subject Code</th>
                  <th style={styles.th}>Marks</th>
                  <th style={styles.th}>Uploaded By</th>
                  <th style={styles.th}>Uploaded At</th>
                </tr>
              </thead>
              <tbody>
                {marks.map((m, idx) => (
                  <tr key={idx}>
                    <td style={styles.td}>{m.subject_code || m.subject}</td>
                    <td style={styles.td}>{m.marks}</td>
                    <td style={styles.td}>{m.uploaded_by || "-"}</td>
                    <td style={styles.td}>
                      {m.uploaded_at
                        ? new Date(m.uploaded_at).toLocaleString()
                        : "-"}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
}

/* Simple inline styles for beginners */
const styles = {
  wrap: {
    maxWidth: 760,
    margin: "0 auto",
    padding: 20,
    fontFamily: "system-ui, -apple-system, 'Segoe UI', Roboto, 'Helvetica Neue', Arial"
  },
  h2: {
    marginBottom: 12
  },
  form: {
    display: "grid",
    gap: 12,
    marginBottom: 16
  },
  label: {
    display: "flex",
    flexDirection: "column",
    fontSize: 14
  },
  input: {
    padding: "8px 10px",
    fontSize: 14,
    marginTop: 6,
    borderRadius: 4,
    border: "1px solid #ccc"
  },
  button: {
    width: 140,
    padding: "8px 12px",
    background: "#1f6feb",
    color: "white",
    border: "none",
    borderRadius: 6,
    cursor: "pointer"
  },
  error: {
    color: "crimson",
    marginTop: 8
  },
  results: {
    marginTop: 18
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: 8
  },
  th: {
    textAlign: "left",
    borderBottom: "2px solid #eee",
    padding: "8px 6px"
  },
  td: {
    padding: "8px 6px",
    borderBottom: "1px solid #f2f2f2"
  }
};
