import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";

import StudentLookup from "./components/StudentLookup";
import StaffUpload from "./components/StaffUpload";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken("");
  };

  return (
    <Router>
      <div style={styles.navbar}>
        <Link style={styles.link} to="/">Student Lookup</Link>
        <Link style={styles.link} to="/upload">Staff Upload</Link>

        {token ? (
          <button onClick={handleLogout} style={styles.logoutBtn}>
            Logout
          </button>
        ) : null}
      </div>

      <div style={styles.container}>
        <Routes>
          <Route path="/" element={<StudentLookup />} />
          <Route
            path="/upload"
            element={<StaffUpload token={token} setToken={setToken} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;

// -----------------------------------
// Simple inline styles for beginners
// -----------------------------------
const styles = {
  navbar: {
    background: "#222",
    padding: "15px",
    display: "flex",
    gap: "20px",
    alignItems: "center"
  },
  link: {
    color: "white",
    textDecoration: "none",
    fontSize: "18px"
  },
  logoutBtn: {
    marginLeft: "auto",
    background: "red",
    color: "white",
    padding: "6px 12px",
    border: "none",
    cursor: "pointer",
    borderRadius: "4px"
  },
  container: {
    padding: "20px"
  }
};
