import React, { useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode";


/* ================= Utils ================= */

function formatBytes(bytes) {
  if (!bytes) return "0 B";
  const units = ["B", "KB", "MB", "GB", "TB"];
  let i = 0;
  let n = bytes;
  while (n >= 1024 && i < units.length - 1) {
    n /= 1024;
    i++;
  }
  return `${n.toFixed(1)} ${units[i]}`;
}

function getToken() {
  return localStorage.getItem("token");
}

function decodeRole(token) {
  try {
    const d = jwtDecode(token);
    return d.role || "staff";
  } catch {
    return "staff";
  }
}

/* ================= Component ================= */

export default function StaffUpload({ token, setToken }) {
  /* ---------- Auth ---------- */
  const [email, setEmail] = useState("admin@example.com");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("staff");
  const [loginError, setLoginError] = useState("");

  /* ---------- Upload ---------- */
  const [file, setFile] = useState(null);
  const [uploadResult, setUploadResult] = useState(null);
  const [uploadError, setUploadError] = useState("");
  const [loading, setLoading] = useState(false);
  const [actionMessage, setActionMessage] = useState("");

  /* ---------- Admin uploads ---------- */
  const [uploads, setUploads] = useState([]);
  const [uploadsLoading, setUploadsLoading] = useState(false);
  const [uploadsError, setUploadsError] = useState("");
  const [cleanupDays, setCleanupDays] = useState(7);

  const loggedIn = Boolean(token || getToken());
  const authToken = token || getToken();

  /* ---------- Init ---------- */
  useEffect(() => {
    if (!token && getToken()) {
      setToken(getToken());
    }
  }, [token, setToken]);

  useEffect(() => {
    if (authToken) {
      const r = decodeRole(authToken);
      setRole(r);
      if (r === "admin") {
        fetchUploads(authToken);
      }
    }
  }, [authToken]);

  /* ================= Auth ================= */

  async function handleLogin(e) {
    e.preventDefault();
    setLoginError("");
    setLoading(true);

    try {
      const res = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password })
      });
      const j = await res.json();

      if (!res.ok) {
        setLoginError(j.error || "Login failed");
        return;
      }

      localStorage.setItem("token", j.token);
      setToken(j.token);
      setRole(decodeRole(j.token));
      setPassword("");
      setActionMessage("Logged in");

    } catch {
      setLoginError("Network error during login");
    } finally {
      setLoading(false);
      setTimeout(() => setActionMessage(""), 2000);
    }
  }

  function handleLogout() {
    localStorage.removeItem("token");
    setToken("");
    setRole("staff");
    setUploads([]);
    setUploadResult(null);
    setActionMessage("Logged out");
    setTimeout(() => setActionMessage(""), 1500);
  }

  /* ================= Upload ================= */

  async function handleUpload(e) {
    e.preventDefault();
    setUploadError("");
    setUploadResult(null);

    if (!file) {
      setUploadError("Choose an Excel file");
      return;
    }

    setLoading(true);
    try {
      const form = new FormData();
      form.append("file", file);

      const res = await fetch("/api/upload-marks", {
        method: "POST",
        headers: { Authorization: `Bearer ${authToken}` },
        body: form
      });

      const j = await res.json();
      if (!res.ok) {
        setUploadError(j.error || "Upload failed");
        return;
      }

      setUploadResult(j);
      setActionMessage("Upload successful");
      if (role === "admin") fetchUploads();

    } catch {
      setUploadError("Network error during upload");
    } finally {
      setLoading(false);
      setTimeout(() => setActionMessage(""), 2500);
    }
  }

  /* ================= Admin APIs ================= */

  async function fetchUploads(t = authToken) {
    if (role !== "admin") return;

    setUploadsLoading(true);
    setUploadsError("");

    try {
      const res = await fetch("/admin/uploads", {
        headers: { Authorization: `Bearer ${t}` }
      });
      const j = await res.json();

      if (!res.ok) {
        setUploadsError(j.error || "Access denied");
        return;
      }

      setUploads(j.uploads || []);
    } catch {
      setUploadsError("Failed to fetch uploads");
    } finally {
      setUploadsLoading(false);
    }
  }

  async function handleDeleteFile(name) {
    if (!window.confirm(`Delete ${name}?`)) return;

    try {
      const res = await fetch("/admin/uploads", {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${authToken}`
        },
        body: JSON.stringify({ filename: name })
      });

      const j = await res.json();
      if (!res.ok) {
        setUploadsError(j.error || "Delete failed");
        return;
      }

      fetchUploads();
    } catch {
      setUploadsError("Delete failed");
    }
  }

  async function handleCleanup() {
    if (!window.confirm(`Delete files older than ${cleanupDays} days?`)) return;

    try {
      const res = await fetch("/admin/cleanup-uploads", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${authToken}`
        },
        body: JSON.stringify({ days: Number(cleanupDays) })
      });

      const j = await res.json();
      if (!res.ok) {
        setUploadsError(j.error || "Cleanup failed");
        return;
      }

      fetchUploads();
    } catch {
      setUploadsError("Cleanup failed");
    }
  }

  /* ================= UI ================= */

  return (
    <div className="center-wrap">
      <div className="card glass fade-in" style={{ maxWidth: 980 }}>
        <h1 className="title">Staff Upload</h1>

        {!loggedIn ? (
          <form onSubmit={handleLogin} className="form-grid small">
            <label>Email
              <input value={email} onChange={e => setEmail(e.target.value)} />
            </label>
            <label>Password
              <input type="password" value={password} onChange={e => setPassword(e.target.value)} />
            </label>
            <button className="btn btn-primary">{loading ? "Logging in..." : "Login"}</button>
            {loginError && <div className="alert error">{loginError}</div>}
          </form>
        ) : (
          <>
            <div className="loggedBar">
              Logged in as <b>{role}</b>
              <button className="btn btn-ghost" onClick={handleLogout}>Logout</button>
            </div>

            <form onSubmit={handleUpload} className="form-grid">
              <input type="file" accept=".xlsx,.xls,.csv" onChange={e => setFile(e.target.files[0])} />
              <button className="btn btn-primary">{loading ? "Uploading..." : "Upload"}</button>
              {actionMessage && <span style={{ color: "lightgreen" }}>{actionMessage}</span>}
            </form>

            {uploadError && <div className="alert error">{uploadError}</div>}
            {uploadResult && <pre>{JSON.stringify(uploadResult, null, 2)}</pre>}

            {/* ================= ADMIN PANEL ================= */}
            {role === "admin" && (
              <div style={{ marginTop: 24 }}>
                <h2>Admin â€” Uploaded files</h2>

                <div className="actions">
                  <input value={cleanupDays} onChange={e => setCleanupDays(e.target.value)} />
                  <button className="btn btn-ghost" onClick={handleCleanup}>Cleanup older</button>
                  <button className="btn btn-ghost" onClick={() => fetchUploads()}>Refresh</button>
                </div>

                {uploadsError && <div className="alert error">{uploadsError}</div>}

                <table className="marks-table">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Size</th>
                      <th>Modified</th>
                      <th></th>
                    </tr>
                  </thead>
                  <tbody>
                    {uploads.map(f => (
                      <tr key={f.name}>
                        <td>{f.name}</td>
                        <td>{formatBytes(f.size_bytes)}</td>
                        <td>{new Date(f.modified_at).toLocaleString()}</td>
                        <td>
                          <button onClick={() => handleDeleteFile(f.name)}>Delete</button>
                        </td>
                      </tr>
                    ))}
                    {uploads.length === 0 && (
                      <tr><td colSpan="4">No uploads found</td></tr>
                    )}
                  </tbody>
                </table>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}
