import React, { useState, useEffect } from "react";

export default function StaffUpload({ token, setToken }) {
  const [email, setEmail] = useState("admin@example.com");
  const [password, setPassword] = useState("");
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loginError, setLoginError] = useState("");
  const [uploadResult, setUploadResult] = useState(null);
  const [uploadError, setUploadError] = useState("");

  useEffect(() => {
    const local = localStorage.getItem("token");
    if (local && !token) setToken(local);
  }, [token, setToken]);

  async function handleLogin(e) {
    e.preventDefault();
    setLoginError("");
    if (!email || !password) {
      setLoginError("Enter email and password.");
      return;
    }
    setLoading(true);
    try {
      const res = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password })
      });
      const j = await res.json();
      if (!res.ok) {
        setLoginError(j.error || "Login failed");
      } else {
        const t = j.token;
        localStorage.setItem("token", t);
        setToken(t);
        setPassword("");
      }
    } catch (err) {
      console.error(err);
      setLoginError("Network error during login.");
    } finally {
      setLoading(false);
    }
  }

  async function handleUpload(e) {
    e.preventDefault();
    setUploadError("");
    setUploadResult(null);

    if (!file) {
      setUploadError("Choose an Excel file to upload.");
      return;
    }
    if (!localStorage.getItem("token") && !token) {
      setUploadError("You must be logged in to upload.");
      return;
    }

    setLoading(true);
    try {
      const form = new FormData();
      form.append("file", file);

      const res = await fetch("/api/upload-marks", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token") || token}`
        },
        body: form
      });

      const j = await res.json();
      if (!res.ok) {
        setUploadError(j.error || j.details || "Upload failed");
      } else {
        setUploadResult(j);
      }
    } catch (err) {
      console.error(err);
      setUploadError("Network error during upload.");
    } finally {
      setLoading(false);
    }
  }

  function handleLogout() {
    localStorage.removeItem("token");
    setToken("");
    setUploadResult(null);
    setUploadError("");
  }

  const loggedIn = Boolean(token || localStorage.getItem("token"));

  return (
    <div className="center-wrap">
      <div className="card glass fade-in">
        <h1 className="title">Staff Upload</h1>

        {!loggedIn ? (
          <form onSubmit={handleLogin} className="form-grid small">
            <label className="label">
              Email
              <input className="input" value={email} onChange={(e) => setEmail(e.target.value)} type="email" />
            </label>

            <label className="label">
              Password
              <input className="input" value={password} onChange={(e) => setPassword(e.target.value)} type="password" />
            </label>

            <div className="actions">
              <button className="btn btn-primary" type="submit" disabled={loading}>
                {loading ? "Logging in..." : "Login"}
              </button>
            </div>

            {loginError && <div className="alert error">{loginError}</div>}
          </form>
        ) : (
          <div>
            <div className="loggedBar">
              <div>Logged in as <b>staff</b></div>
              <button className="btn btn-ghost" onClick={handleLogout}>Logout</button>
            </div>

            <form onSubmit={handleUpload} className="form-grid">
              <label className="label">
                Upload Excel file (marks)
                <input className="file-input" type="file" accept=".xlsx,.xls" onChange={(e) => setFile(e.target.files[0])} />
              </label>

              <div className="actions">
                <button className="btn btn-primary" type="submit" disabled={loading}>
                  {loading ? "Uploading..." : "Upload"}
                </button>
              </div>
            </form>

            {uploadError && <div className="alert error">{uploadError}</div>}
            {uploadResult && (
              <div className="resultBox">
                <h4>Upload result</h4>
                <pre>{JSON.stringify(uploadResult, null, 2)}</pre>
              </div>
            )}

            <div className="muted note">Note: Excel must contain columns: <code>register_number</code>, <code>subject_code</code>, <code>marks</code>. Optional: <code>dob</code>, <code>student_name</code>.</div>
          </div>
        )}
      </div>
    </div>
  );
}
