import React, { useState, useEffect } from "react";
import jwtDecode from "jwt-decode";


/**
 * StaffUpload with Admin Uploads management.
 *
 * - Login as staff (existing)
 * - Upload marks file (existing)
 * - If logged in as admin, show Uploads table with Delete / Download / Cleanup
 *
 * Expects backend admin endpoints:
 *  GET  /admin/uploads
 *  DELETE /admin/uploads { filename }
 *  POST /admin/cleanup-uploads { days }
 *
 * Token is stored in localStorage.token (same as earlier code).
 */

function formatBytes(bytes) {
  if (!bytes) return "0 B";
  const units = ["B", "KB", "MB", "GB", "TB"];
  let i = 0;
  let n = bytes;
  while (n >= 1024 && i < units.length - 1) {
    n /= 1024;
    i++;
  }
  return `${n.toFixed(1)} ${units[i]}`;
}

export default function StaffUpload({ token, setToken }) {
  // auth & forms
  const [email, setEmail] = useState("admin@example.com");
  const [password, setPassword] = useState("");
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loginError, setLoginError] = useState("");
  const [uploadResult, setUploadResult] = useState(null);
  const [uploadError, setUploadError] = useState("");

  // admin uploads management
  const [uploads, setUploads] = useState([]);
  const [uploadsLoading, setUploadsLoading] = useState(false);
  const [uploadsError, setUploadsError] = useState("");
  const [cleanupDays, setCleanupDays] = useState(7);
  const [actionMessage, setActionMessage] = useState("");

  useEffect(() => {
    const local = localStorage.getItem("token");
    if (local && !token) setToken(local);
  }, [token, setToken]);

  useEffect(() => {
    if (token) fetchUploads();
  }, [token]);

  // --- Auth / Login ---
  async function handleLogin(e) {
    e.preventDefault();
    setLoginError("");
    if (!email || !password) {
      setLoginError("Enter email and password.");
      return;
    }
    setLoading(true);
    try {
      const res = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password })
      });
      const j = await res.json();
      if (!res.ok) {
        setLoginError(j.error || "Login failed");
      } else {
        const t = j.token;
        localStorage.setItem("token", t);
        setToken(t);
        setPassword("");
        setActionMessage("Logged in");
        // fetch uploads for admin view
        fetchUploads(t);
      }
    } catch (err) {
      console.error(err);
      setLoginError("Network error during login.");
    } finally {
      setLoading(false);
      setTimeout(() => setActionMessage(""), 2000);
    }
  }

  function getAuthHeader(providedToken) {
    const t = providedToken || localStorage.getItem("token") || token;
    return t ? { Authorization: `Bearer ${t}` } : {};
  }

  // --- Upload marks file ---
  async function handleUpload(e) {
    e.preventDefault();
    setUploadError("");
    setUploadResult(null);

    if (!file) {
      setUploadError("Choose an Excel file to upload.");
      return;
    }
    if (!localStorage.getItem("token") && !token) {
      setUploadError("You must be logged in to upload.");
      return;
    }

    setLoading(true);
    try {
      const form = new FormData();
      form.append("file", file);

      const res = await fetch("/api/upload-marks", {
        method: "POST",
        headers: {
          ...getAuthHeader()
        },
        body: form
      });

      const j = await res.json();
      if (!res.ok) {
        setUploadError(j.error || j.details || "Upload failed");
      } else {
        setUploadResult(j);
        setActionMessage("Upload successful");
        // refresh uploads list
        fetchUploads();
      }
    } catch (err) {
      console.error(err);
      setUploadError("Network error during upload.");
    } finally {
      setLoading(false);
      setTimeout(() => setActionMessage(""), 2500);
    }
  }

  function handleLogout() {
    localStorage.removeItem("token");
    setToken("");
    setUploadResult(null);
    setUploadError("");
    setUploads([]);
    setUploadsError("");
    setActionMessage("Logged out");
    setTimeout(() => setActionMessage(""), 1500);
  }

  // --- Admin: fetch upload list ---
  async function fetchUploads(providedToken) {
    const t = providedToken || localStorage.getItem("token") || token;
    if (!t) return;
    setUploadsLoading(true);
    setUploadsError("");
    try {
      const res = await fetch("/admin/uploads", {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${t}`
        }
      });
      const j = await res.json();
      if (!res.ok) {
        setUploadsError(j.error || "Failed to load uploads");
      } else {
        setUploads(j.uploads || []);
      }
    } catch (err) {
      console.error(err);
      setUploadsError("Network error while fetching uploads");
    } finally {
      setUploadsLoading(false);
    }
  }

  // --- Admin: delete a single file ---
  async function handleDeleteFile(fname) {
    if (!confirm(`Delete uploaded file "${fname}"? This cannot be undone.`)) return;
    setActionMessage("");
    setUploadsError("");
    try {
      const res = await fetch("/admin/uploads", {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
          ...getAuthHeader()
        },
        body: JSON.stringify({ filename: fname })
      });
      const j = await res.json();
      if (!res.ok) {
        setUploadsError(j.error || "Delete failed");
      } else {
        setActionMessage(`Deleted ${fname}`);
        // refresh
        fetchUploads();
      }
    } catch (err) {
      console.error(err);
      setUploadsError("Network error while deleting file");
    } finally {
      setTimeout(() => setActionMessage(""), 2500);
    }
  }

  // --- Admin: cleanup older than N days ---
  async function handleCleanup() {
    if (!confirm(`Delete files older than ${cleanupDays} day(s)?`)) return;
    setUploadsError("");
    setActionMessage("Cleaning...");
    try {
      const res = await fetch("/admin/cleanup-uploads", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          ...getAuthHeader()
        },
        body: JSON.stringify({ days: Number(cleanupDays || 7) })
      });
      const j = await res.json();
      if (!res.ok) {
        setUploadsError(j.error || "Cleanup failed");
      } else {
        setActionMessage(`Removed ${j.removed?.length || 0} files`);
        fetchUploads();
      }
    } catch (err) {
      console.error(err);
      setUploadsError("Network error while cleanup");
    } finally {
      setTimeout(() => setActionMessage(""), 3000);
    }
  }

  const loggedIn = Boolean(token || localStorage.getItem("token"));

  return (
    <div className="center-wrap">
      <div className="card glass fade-in" style={{ maxWidth: 980 }}>
        <h1 className="title">Staff Upload</h1>

        {!loggedIn ? (
          <form onSubmit={handleLogin} className="form-grid small">
            <label className="label">
              Email
              <input className="input" value={email} onChange={(e) => setEmail(e.target.value)} type="email" />
            </label>

            <label className="label">
              Password
              <input className="input" value={password} onChange={(e) => setPassword(e.target.value)} type="password" />
            </label>

            <div className="actions">
              <button className="btn btn-primary" type="submit" disabled={loading}>
                {loading ? "Logging in..." : "Login"}
              </button>
            </div>

            {loginError && <div className="alert error">{loginError}</div>}
          </form>
        ) : (
          <div>
            <div className="loggedBar">
              <div>Logged in as <b>staff</b></div>
              <div style={{ display: "flex", gap: 8 }}>
                <button className="btn btn-ghost" onClick={handleLogout}>Logout</button>
              </div>
            </div>

            <form onSubmit={handleUpload} className="form-grid">
              <label className="label">
                Upload Excel file (marks)
                <input className="file-input" type="file" accept=".xlsx,.xls,.csv" onChange={(e) => setFile(e.target.files[0])} />
              </label>

              <div className="actions">
                <button className="btn btn-primary" type="submit" disabled={loading}>
                  {loading ? "Uploading..." : "Upload"}
                </button>
                <div style={{ marginLeft: "auto", color: "var(--muted)", alignSelf: "center" }}>
                  {actionMessage && <span style={{ color: "lightgreen", fontWeight: 600 }}>{actionMessage}</span>}
                </div>
              </div>
            </form>

            {uploadError && <div className="alert error">{uploadError}</div>}
            {uploadResult && (
              <div className="resultBox" style={{ marginTop: 12 }}>
                <h4>Upload result</h4>
                <pre style={{ whiteSpace: "pre-wrap" }}>{JSON.stringify(uploadResult, null, 2)}</pre>
              </div>
            )}

            {/* ADMIN PANEL */}
            <div style={{ marginTop: 18 }}>
              <h2 style={{ marginBottom: 8 }}>Admin â€” Uploaded files</h2>

              <div style={{ display: "flex", gap: 10, alignItems: "center", marginBottom: 12 }}>
                <input
                  className="input"
                  style={{ width: 120 }}
                  value={cleanupDays}
                  onChange={(e) => setCleanupDays(e.target.value.replace(/\D/g, ""))}
                  placeholder="days"
                />
                <button className="btn btn-ghost" onClick={handleCleanup}>Cleanup older</button>
                <button className="btn btn-ghost" onClick={() => fetchUploads()}>Refresh</button>
                <div style={{ marginLeft: "auto", color: "var(--muted)" }}>
                  {uploadsLoading ? "Loading..." : `${uploads.length} files`}
                </div>
              </div>

              {uploadsError && <div className="alert error">{uploadsError}</div>}

              <div style={{ overflowX: "auto", borderRadius: 8 }}>
                <table className="marks-table" style={{ minWidth: 720 }}>
                  <thead>
                    <tr>
                      <th>Filename</th>
                      <th>Size</th>
                      <th>Modified</th>
                      <th style={{ textAlign: "right" }}>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {uploads.map((f) => (
                      <tr key={f.name}>
                        <td style={{ fontWeight: 600 }}>{f.name}</td>
                        <td>{formatBytes(f.size_bytes)}</td>
                        <td>{new Date(f.modified_at).toLocaleString()}</td>
                        <td style={{ textAlign: "right" }}>
                          <a
                            className="btn btn-ghost"
                            href={`/admin/uploads/download/${encodeURIComponent(f.name)}`}
                            style={{ marginRight: 8 }}
                          >
                            Download
                          </a>
                          <button className="btn btn-ghost" onClick={() => handleDeleteFile(f.name)}>Delete</button>
                        </td>
                      </tr>
                    ))}
                    {uploads.length === 0 && (
                      <tr>
                        <td colSpan={4} style={{ padding: 16 }} className="muted">No uploads found</td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </div>

            <div style={{ marginTop: 12 }} className="muted note">
              Note: Excel must contain columns: <code>register_number</code>, <code>subject_code</code>, <code>marks</code>. Optional: <code>dob</code>, <code>student_name</code>.
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
